import React, { useRef, useEffect } from "react";
import * as d3 from "d3";

const BarChart = ({ data, width, height }) => {
  const svgRef = useRef();

  useEffect(() => {
    const svg = d3.select(svgRef.current);

    svg.selectAll("rect")
      .data(data)
      .join("rect")
      .attr("x", (d, i) => i * 70)
      .attr("y", d => height - d * 10)
      .attr("width", 65)
      .attr("height", d => d * 10)
      .attr("fill", "blue");
  }, [data, height, width]);

  return (
    <svg ref={svgRef} width={width} height={height}>
      {data.map((d, i) => (
        <rect key={i} x={i * 70} y={height - d * 10} width={65} height={d * 10} fill="blue" />
      ))}
    </svg>
  );
};

export default BarChart;
